mapper.update:
update post 
set
	po_title = #{post.po_title},
	po_content = #{post.po_content}
where
	po_key = #{post.po_key}

mapper.select:
select post.*, bo_name as po_bo_name 
from post
join board on po_bo_key = bo_key 
where po_upd < now()

	and po_bo_key = #{criteria.bo_key}



		and (po_title like concat('%', #{criteria.search}, '%')
			or po_content like concat('%', #{criteria.search}, '%')
			or po_me_id = #{criteria.search}
		)


		and (po_title like concat('%', #{criteria.search}, '%')
		)


		and (po_title like concat('%', #{criteria.search}, '%')
			or po_content like concat('%', #{criteria.search}, '%')
		)


		and po_me_id = #{criteria.search}


order by po_key desc
limit #{criteria.pageStart},#{criteria.perPageNum}

select count(*) from post where po_upd < now()
and po_bo_key = #{criteria.po_bo_key}
and (po_title like concat('%', #{criteria.search}, '%') or po_content like concat('%', #{criteria.search}, '%') or po_us_key = #{criteria.search})
and (po_title like concat('%', #{criteria.search}, '%') or po_content like concat('%', #{criteria.search}, '%'))
and po_us_key = #{criteria.search}
